    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.all_inputs);

        ArrayAdapter<String> CitiesAdapter1 = new ArrayAdapter<>(this,
                android.R.layout.simple_dropdown_item_1line, Cities);

        CitiesList1 = findViewById(R.id.cities_list1);
        CitiesList1.setAdapter(CitiesAdapter1);


        ArrayAdapter<String> CitiesAdapter2 = new ArrayAdapter<>(this,
                android.R.layout.simple_dropdown_item_1line, Cities);

        CitiesList2 = findViewById(R.id.cities_list2);
        CitiesList2.setAdapter(CitiesAdapter2);

        ExpectedSalary=findViewById(R.id.ExpectedSalary);
//###############################################
        String[] LocationOptions = new String[]{"Select","In City","Out of City"};

        ArrayAdapter<String> Location = new ArrayAdapter<String>(this,
                android.R.layout.simple_spinner_dropdown_item, LocationOptions);


        Location1 = findViewById(R.id.Location);
        Location1.setAdapter(Location);
        Location1.setOnTouchListener(new View.OnTouchListener() {

            @Override
            public boolean onTouch(View v, MotionEvent event) {
                InputMethodManager imm=(InputMethodManager)getApplicationContext().getSystemService(Context.INPUT_METHOD_SERVICE);
                imm.hideSoftInputFromWindow(Bedrooms1.getWindowToken(), 0);
                return false;
            }
        }) ;

        Bedrooms1 = findViewById(R.id.Bedrooms);

        RentInput1 = findViewById(R.id.RentInput);
        Utilities1 = findViewById(R.id.Utilities);
//###############################################

        String[] TransportOptions = new String[]{"Select","Public", "Personal"};

        ArrayAdapter<String> TransportAdapter = new ArrayAdapter<String>(this,
                android.R.layout.simple_spinner_dropdown_item, TransportOptions);

        Transportation = findViewById(R.id.TransportList);

        Transportation.setAdapter(TransportAdapter);
        Transportation.setOnTouchListener(new View.OnTouchListener() {

            @Override
            public boolean onTouch(View v, MotionEvent event) {
                InputMethodManager imm=(InputMethodManager)getApplicationContext().getSystemService(Context.INPUT_METHOD_SERVICE);
                imm.hideSoftInputFromWindow(Utilities1.getWindowToken(), 0);
                return false;
            }
        }) ;

        Grocery=findViewById(R.id.Grocery);

        Clothes = findViewById(R.id.Clothes);

        TransportationCost = findViewById(R.id.TransportationCost);

//###############################################

        DiningOut=findViewById(R.id.DiningOut);
        Alcohol=findViewById(R.id.Alcohol);
        Fitness = findViewById(R.id.Fitness);
        Other=findViewById(R.id.Other);

        EntertainmentButton = findViewById(R.id.EntertainmentButton);

        EntertainmentButton.setOnClickListener(this);

//###############################################

    }


    public void onClick(View a){

        String City1 = CitiesList1.getText().toString();
        String City2 = CitiesList2.getText().toString();
        String Salary= ExpectedSalary.getText().toString();

        if (!Arrays.asList(Cities).contains(City1)) {
            Toast CurrentError = Toast.makeText(AllInputs.this, "Please Enter A Supported Current City", Toast.LENGTH_LONG);
            CurrentError.setGravity(Gravity.CENTER, 0, -1500);
            CurrentError.show();
            return;
        }

        if (!Arrays.asList(Cities).contains(City2)) {
            Toast DestinationError = Toast.makeText(AllInputs.this, "Please Enter A Supported Destination City", Toast.LENGTH_LONG);
            DestinationError.setGravity(Gravity.CENTER, 0, -1500);
            DestinationError.show();
            return;
        }

        if (Salary.equals("")) {
            Toast DestinationError = Toast.makeText(AllInputs.this, "Sorry! Mr.Krabs don't speak broke, Please enter a salary", Toast.LENGTH_LONG);
            DestinationError.setGravity(Gravity.CENTER, 0, -1500);
            DestinationError.show();
            return;
        }

        ((GlobalClass) this.getApplication()).setCurrentCity(City1);
        ((GlobalClass) this.getApplication()).setDestinationCity(City2);
        ((GlobalClass) this.getApplication()).setExpectedSalary(Salary);


//###############################################
        String Bedrooms = Bedrooms1.getText().toString();
        String Location = Location1.getSelectedItem().toString();
        String Rent= RentInput1.getText().toString();
        String Utilities= Utilities1.getText().toString();

        if ((Bedrooms.equals(""))||(Location.equals("Select"))||(Rent.equals(""))||(Utilities.equals(""))) {
            Toast DestinationError = Toast.makeText(AllInputs.this, "Please remember to enter a value for all fields, if not applicable enter 0", Toast.LENGTH_LONG);
            DestinationError.setGravity(Gravity.CENTER, 0, -1500);
            DestinationError.show();
            return;
        }

        ((GlobalClass) this.getApplication()).setRentPerMonth(Rent);
        ((GlobalClass) this.getApplication()).setBedrooms(Bedrooms);
        ((GlobalClass) this.getApplication()).setLocation(Location);
        ((GlobalClass) this.getApplication()).setUtilities(Utilities);


//###############################################

        String Grocery1 = Grocery.getText().toString();
        String Clothes1 = Clothes.getText().toString();
        String TransportationCost1= TransportationCost.getText().toString();
        String Transportation1= Transportation.getSelectedItem().toString();

        if ((Grocery1.equals(""))||(Clothes1.equals(""))||(TransportationCost1.equals(""))||(Transportation1.equals("Select"))) {
            Toast DestinationError = Toast.makeText(AllInputs.this, "Please remember to enter a value for all fields, if not applicable enter 0", Toast.LENGTH_LONG);
            DestinationError.setGravity(Gravity.CENTER, 0, -1500);
            DestinationError.show();
            return;
        }

        ((GlobalClass) this.getApplication()).setGrocery(Grocery1);
        ((GlobalClass) this.getApplication()).setClothes(Clothes1);
        ((GlobalClass) this.getApplication()).setTransportationCost(TransportationCost1);
        ((GlobalClass) this.getApplication()).setTransportation(Transportation1);


        String Diningout1 = DiningOut.getText().toString();
        String Alcohol1 = Alcohol.getText().toString();
        String Fitness1= Fitness.getText().toString();
        String Other1= Other.getText().toString();

        if ((Diningout1.equals(""))||(Alcohol1.equals(""))||(Fitness1.equals(""))||(Other1.equals(""))) {
            Toast DestinationError = Toast.makeText(AllInputs.this, "Please remember to enter a value for all fields, if not applicable enter 0", Toast.LENGTH_LONG);
            DestinationError.setGravity(Gravity.CENTER, 0, -1500);
            DestinationError.show();
            return;
        }

        ((GlobalClass) this.getApplication()).setDining(Diningout1);
        ((GlobalClass) this.getApplication()).setAlcohol(Alcohol1);
        ((GlobalClass) this.getApplication()).setFitness(Fitness1);
        ((GlobalClass) this.getApplication()).setMisc(Other1);
//###############################################

        GoToResults();


    }
